'    l_html_string := l_html_string || ''class="text_field apex-item-text"''||case when p_is_readonly then ''disabled="disabled"'' else '''' end || ',
'    coalesce(p_item.element_attributes, '''') || '' />'';',
'    --',
'    sys.htp.prn(l_html_string);',
'    --',
'  end if;',
'  ',
'    apex_css.add_file (',
'          p_name      => ''jquery-ui'',',
'          p_directory => p_plugin.file_prefix );',
'    apex_css.add_file (',
'          p_name      => ''ItemSpinner'',',
'          p_directory => p_plugin.file_prefix );',
'    apex_javascript.add_library(',
'          p_name => ''ItemSpinner'',',
'          p_directory => p_plugin.file_prefix,',
'          p_version => null);',
'    apex_javascript.add_library(',
'          p_name => ''jquery-ui'',',
'          p_directory => p_plugin.file_prefix,',
'          p_version => null);',
'    apex_javascript.add_library(',
'          p_name => ''jquery.mousewheel'',',
'          p_directory => p_plugin.file_prefix,',
'          p_version => null);',
'    apex_javascript.add_library(',
'          p_name => ''globalize'',',
'          p_directory => p_plugin.file_prefix,',
'          p_version => null);',
'    apex_javascript.add_library(',
'          p_name => ''globalize.culture.de-DE'',',
'          p_directory => p_plugin.file_prefix,',
'          p_version => null);',
'     apex_javascript.add_library(',
